    body:
      "<div style='width: 540px; margin: 0 auto; font: normal 14px Helvetica,\"Helvetica Neue\", Arial, sans-serif; color: #333; line-height: 20px;'><div style='text-align: right; margin: 22px 0 42px;'><img alt='payever logo' src='cid:logo.png' /></div><style>.report {font-family: 'Trebuchet MS', Arial, Helvetica, sans-serif;border-collapse: collapse;width: 100%;}.report td, .report th {border: 1px solid #ddd;padding: 8px;}.report tr:nth-child(even){background-color: #f2f2f2;}.report tr:hover {background-color: #ddd;}.report th {padding-top: 12px;padding-bottom: 12px;text-align: left;background-color: #000000;color: white;}</style><p><h1 style='color:#2b2301;'>Daily Transactions Report {{ report.date | format_date }}</h1></p><p><h2 style='color:#8C8C8C;'>Transactions by Currency</h2><table class='report'><thead><tr><th>Today Total</th><th>Yesterday Overall Total</th><th>Overall Total</th><th>Currency</th><th>Exchange Rate</th></tr></thead><tbody>{% for item in report.report_items %}<tr><td>{{ item.todayTotal|number_format_currency(item.currency)  }}</td><td>{{ item.overallTotal|number_format_currency(item.currency)  }}</td><td>{{ item.total|number_format_currency(item.currency)  }}</td><td>{{ item.currency }}</td><td>{{ item.exchangeRate }}</td></tr>{% endfor %}</tbody></table></p><p><h2 style='color:#8C8C8C;'>Transactions</h2><table class='report'><thead><tr><th>Today Total</th><th>Yesterday Overall Total</th><th>Overall Total</th></tr></thead><tbody>{% for item in report.report_items_exchanged %}<tr><td>{{ item.todayTotal|number_format_currency(item.currency)  }}</td><td>{{ item.overallTotal|number_format_currency(item.currency)  }}</td><td>{{ item.total|number_format_currency(item.currency)  }}</td></tr>{% endfor %}</tbody></table></p>{% for item in report.report_items %}<p><h2 style='color:#8C8C8C;'>{{ item.currency }} transactions by Payment Option </h2><table class='report'><thead><tr><th>Payment Option</th><th>Today Total</th><th>Yesterday Overall Total</th><th>Overall Total</th></tr></thead><tbody>{% for itemPayment in item.paymentOption %}<tr><td>{{ itemPayment.paymentOption }}</td><td>{{ itemPayment.todayTotal|number_format_currency(item.currency)  }}</td><td>{{ itemPayment.overallTotal|number_format_currency(item.currency)  }}</td><td>{{ itemPayment.total|number_format_currency(item.currency)  }}</td></tr>{% endfor %}<tfoot><tr><th>TOTAL</th><th>{{ item.todayTotal|number_format_currency(item.currency)  }}</th><th>{{ item.overallTotal|number_format_currency(item.currency)  }}</th><th>{{ item.total|number_format_currency(item.currency)  }}</th></tr></tfoot></tbody></table>{% endfor %}</p><p>{% block footer %}</p><p>Best regards<br />Your payever Team</p><p>{% endblock footer %}</p></div>",
